document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){console.log(t);var e=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${e}]`),a=document.querySelectorAll("[data-tab-id]");for(let t=0;t<a.length;t++)a[t].classList.remove("scenes__container--is-active");e.classList.add("scenes__container--is-active");var n=document.querySelectorAll("[data-tab-button]");for(let t=0;t<n.length;t++)n[t].classList.remove("scenes__tabs__button--is-active");t.target.classList.add("scenes__tabs__button--is-active")})});